import React from 'react';

function PopupWithForm(props) {
  return (
    <div className={`popup popup_type_${props.name}`+''+(isOpen?'popup_opened':'')}>
      <div className="popup__container-general popup__container-confirm popup__container-avatar">
        <button className="popup__close-button" type="button"></button>
         <p className="popup__title">{popupTitle}</p>
        <form className={`popup__form popup__form_type_${props.name}`} name="form" novalidate>
          {children}
          <button className="popup__button" type="submit">{textButton}</button>
        </form>
      </div>
    </div>
  );
}

export default PopupWithForm;
