import React from 'react';
import api from '../utils/Api';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import Card from './Card';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import { CardContext } from '../contexts/CardContext';

function App() {
  const [isEditProfilePopupOpen, setisEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setisAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setisEditAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getProfile()
    .then((userData) => {
      setCurrentUser(userData);
    })
    api.getCards()
    .then((data) => {
      setCards(
        data.map((item) => ({
          id: item._id,
          link: item.link,
          name: item.name,
          likes: item.likes,
          owner: item.owner
        }))
      );
    })
    .catch((error)=>{
      console.log(error);
    })
  }, []);

  function closeAllPopup() {
    setisEditProfilePopupOpen(false);
    setisAddPlacePopupOpen(false);
    setisEditAvatarPopupOpen(false);
    setSelectedCard({});
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
  //   api.addLike(card._id).then((newCard) => {
  //     setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
  // });

    if (!isLiked) {
      api.addLike(card.id)
      .then((newCard) => {
          setCards((state) => state.map((c) => c.id === card.id ? newCard : c));
      })
      .catch ((error) => {
          console.log (error);
      })
    } else {
      api.removeLike(card.id)
      .then ((newCard) => {
          setCards((state) => state.map((c) => c.id === card.id ? newCard : c));
      })
      .catch ((error) => {
          console.log(error);
      })
    }
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <CardContext.Provider value={cards}>
        <div className="main-page">
          <div className="page-container">
            <Header />
            <Main
              cards={cards}
              onEditProfile={setisEditProfilePopupOpen}
              onAddPlace={setisAddPlacePopupOpen}
              onEditAvatar={setisEditAvatarPopupOpen}
              onCardClick={setSelectedCard}
              onCardLike={handleCardLike}
            />
            <Footer />
            <PopupWithForm
              name='profile'
              container='general'
              popupTitle='Редактировать профиль'
              isOpen={isEditProfilePopupOpen}
              onClose={closeAllPopup}
              textButton='Сохранить'>
              <input id="input-name" className="popup__input popup__input_type_name"
              type="text" name="name" minLength="2" maxLength="40" required />
              <span className="input-name-error popup__error"></span>
              <input id="input-career" className="popup__input popup__input_type_other"
              type="text" name="about" minLength="2" maxLength="200" required />
              <span className="input-career-error popup__error"></span>
            </PopupWithForm>
            <PopupWithForm
              name='card'
              container='general'
              popupTitle='Новое место'
              isOpen={isAddPlacePopupOpen}
              onClose={closeAllPopup}
              textButton='Создать'>
              <input id="input-place" className="popup__input popup__input_type_name"
              type="text" placeholder="Название" name="name" minLength="2" maxLength="30" required />
              <span className="input-place-error popup__error"></span>
              <input id="input-url" className="popup__input popup__input_type_other"
              type="url" placeholder="Ссылка на картинку" name="link" required />
              <span className="input-url-error popup__error"></span>
            </PopupWithForm>
            <PopupWithForm
              name='avatar'
              container='avatar'
              popupTitle='Обновить аватар'
              isOpen={isEditAvatarPopupOpen}
              onClose={closeAllPopup}
              textButton='Сохранить'>
              <input id="input-url-avatar" className="popup__input popup__input_type_other"
              type="url" placeholder="Ссылка на картинку" name="avatar" required />
              <span className="input-url-avatar-error popup__error"></span>
            </PopupWithForm>
            <ImagePopup
              card={selectedCard}
              onClose={closeAllPopup}>
            </ImagePopup>
          </div>
        </div>
      </CardContext.Provider>
    </CurrentUserContext.Provider>
  );
}

export default App;
