import { Link, useHistory } from "react-router-dom";
import * as Auth from './Auth.js';
import App from "./App";
import React from "react";

function Register({ submitButton }) {

  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');

  function handleSubmit(e) {
    e.preventDefault();
    submitButton(email, password);
  }

  return (
    <div className="start">
      <h1 className="start__title">{props.startTitle}</h1>
      <form className="start__form" name="form" noValidate>
        <input id="" className="start__input" type="email" placeholder="Email" required />
        <input id="" className="start__input" type="password" placeholder="Пароль" minLength="8" maxLength="15" required />
        <button className="start__button" type="submit" onClick = {props.submitButton}>{props.textButton}</button>
      </form>
      <div className="start__sign-in">
        <p className="start__question">Уже зарегистрированы?</p>
        <Link to="/sign-in" className="start__link">Войти</Link>
      </div>
    </div>
  );
}

export default Register;
