import React from 'react';
import api from '../utils/Api';

function Main(props) {
  const [userName, setUserName] = React.useState([`Жак Иф Кусто`]);
  const [userDescription, setUserDescription] = React.useState([`Исследователь океанов`]);
  const [userAvatar, setUserAvatar] = React.useState(0);
  const [cards, setСards] = React.useState([]);

  React.useEffect(() => {
    Promise.all([ api.getProfile(), api.getCards() ])
    .then(([info, data])=>{
      // userId = info._id;
      // cardSection.renderItems(data);
      // createProfileValues.setUserInfo(info);
      setUserName(info.name);
      setUserDescription(info.about);
      setUserAvatar(info.avatar);
      setСards(
        data.map((item) => ({
          id: item._id,
          link: item.link,
          name: item.name,
          likes: item.likes
        }))
      );
    })
    .catch((error)=>{
      console.log(error);
    })
  });

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__card">
          <div className="profile__avatar-group">
            <button className="profile__avatar-button" type="button" aria-label="Изменить аватар" onClick={()=>{props.onEditAvatar(true)}}>
              <div className="profile__avatar-vector"></div>
            </button>
            <img className="profile__avatar" src={ `${userAvatar}` } alt="Аватар" />
          </div>
          <div className="profile__info">
            <div className="profile__name">
              <h1 className="profile__name-text">{userName}</h1>
              <button className="profile__name-button" type="button" onClick={()=>{props.onEditProfile(true)}}></button>
            </div>
            <p className="profile__profession">{userDescription}</p>
          </div>
        </div>
        <button className="profile__button" type="button" onClick={()=>{props.onAddPlace(true)}}></button>
      </section>
      <section className="elements">
        <div className="card">
          <a className="card__image-link">
            <img className="card__image" src="#" alt="Изображение" />
          </a>
          <button className="card__trash"></button>
          <div className="card__items">
            <h2 className="card__name">{card.item.name}</h2>
            <div className="card__like-group">
              <button className="card__like" type="button"></button>
              <p className="card__like-number"></p>
            </div>
          </div>
        </div>
      </section>
    </main>
  );
}

export default Main;
