import React from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';

function App() {
  const [isEditProfilePopupOpen, setisEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setisAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setisEditAvatarPopupOpen] = React.useState(false);

  return (
    <div class="main-page">
      <div class="page-container">
        <Header />
        <Main
          onEditProfile={setisEditProfilePopupOpen}
          onAddPlace={setisAddPlacePopupOpen}
          onEditAvatar={setisEditAvatarPopupOpen}
        />
        <Footer />
        <PopupWithForm
          name='profile'
          container='general'
          popupTitle='Редактировать профиль'
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopup}
          textButton='Сохранить'>
          <input id="input-name" class="popup__input popup__input_type_name"
          type="text" name="name" minlength="2" maxlength="40" required />
          <span class="input-name-error popup__error"></span>
          <input id="input-career" class="popup__input popup__input_type_other"
          type="text" name="about" minlength="2" maxlength="200" required />
          <span class="input-career-error popup__error"></span>
        </PopupWithForm>
        <PopupWithForm
          name='card'
          container='general'
          popupTitle='Новое место'
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopup}
          textButton='Создать'>
          <input id="input-place" class="popup__input popup__input_type_name"
          type="text" placeholder="Название" name="name" minlength="2" maxlength="30" required />
          <span class="input-place-error popup__error"></span>
          <input id="input-url" class="popup__input popup__input_type_other"
          type="url" placeholder="Ссылка на картинку" name="link" required />
          <span class="input-url-error popup__error"></span>
        </PopupWithForm>
        <PopupWithForm
          name='avatar'
          container='avatar'
          popupTitle='Обновить аватар'
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopup}
          textButton='Сохранить'>
          <input id="input-url-avatar" class="popup__input popup__input_type_other"
          type="url" placeholder="Ссылка на картинку" name="avatar" required />
          <span class="input-url-avatar-error popup__error"></span>
        </PopupWithForm>
        <ImagePopup />
      </div>
    </div>
  );
}




export default App;
